import React from 'react';
import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";

// Registre as fontes que você usará
pdfMake.vfs = pdfFonts.pdfMake.vfs;

const SaleReceipt = ({ sales, amount, clientname }) => {
  const Title = {
    text: "Recibo de Pagamento",
    fontSize: 15,
    bold: true,
    margin: [15, 20, 0, 45]
  };

  const imageUrl = 'data:image/png;base64,';
  const tableContent = {
    table: {
      widths: ['30%', '70%'], 
      body: [
        [{ image: imageUrl, width: 100, border: [ true] }, 'Conteúdo da Coluna 2'], // Inserir a imagem na primeira coluna
      ]
    }
  };

  const docDefinition = {
    pageSize: 'A4',
    header: [Title],
    content: [tableContent], 
    defaultStyle: {
      border: [true, true, true, true],
      borderColor: 'black' 
    }
  };


    pdfMake.createPdf(docDefinition).download();


 
};

export default SaleReceipt;
